import * as React from 'react'
import { ArrowLeft } from 'lucide-react'

export function LeadingIconButtons() {
  return (
    <button
      type="button"
      className="inline-flex items-center rounded-md bg-black px-3 py-2 text-sm font-semibold text-white hover:bg-black/80"
    >
      <ArrowLeft className="mr-2 h-4 w-4" />
      Button Text
    </button>
  )
}

import * as React from 'react'
import { ArrowRight } from 'lucide-react'

export function TrailingIconButtons() {
  return (
    <button
      type="button"
      className="inline-flex items-center rounded-md bg-black px-3 py-2 text-sm font-semibold text-white hover:bg-black/80"
    >
      Button Text
      <ArrowRight className="ml-2 h-4 w-4" />
    </button>
  )
}

import * as React from 'react'

export function RoundedButtons() {
  return (
    <button
      type="button"
      className="rounded-full bg-black px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-black/80 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-black"
    >
      Button text
    </button>
  )
}

import * as React from 'react'
import { Plus } from 'lucide-react'

export function IconButtons() {
  return (
    <button
      type="button"
      className="rounded-full bg-black px-3 py-3 text-sm font-semibold text-white shadow-sm hover:bg-black/80 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-black"
    >
      <Plus className="h-4 w-4" />
    </button>
  )
}

import React from 'react'

export function DefaultInput() {
  return (
    <div className="w-full md:w-1/3">
      <input
        className="flex h-10 w-full rounded-md border border-black/30 bg-transparent px-3 py-2 text-sm placeholder:text-gray-600 focus:outline-none focus:ring-1 focus:ring-black/30 focus:ring-offset-1 disabled:cursor-not-allowed disabled:opacity-50"
        type="email"
        placeholder="Email"
      ></input>
    </div>
  )
}

import React from 'react'

export function DisabledInput() {
  return (
    <div className="w-full md:w-1/3">
      <input
        className="flex h-10 w-full rounded-md border border-black/30 bg-transparent px-3 py-2 text-sm placeholder:text-gray-600 focus:outline-none focus:ring-1 focus:ring-black/30 focus:ring-offset-1 disabled:cursor-not-allowed disabled:opacity-50"
        type="email"
        placeholder="Email"
        disabled
      ></input>
    </div>
  )
}
